class fup:
    def __init__(self):
        self.name = ''
        self.icon = ''
        self.desc = ''
        self.cost = 0
        self.xp = 0
        self.rank = 0
        
      
    def __repr__(self):  
        return f"{self.name:<20}{self.icon:<30}{self.desc:<50}{self.cost:<8}{self.xp:<8}{self.rank:<8}"
    
    def __str__(self):
        return f"{self.name:<30}{self.cost:<8}{self.xp:<8}{self.rank:<3}{self.icon:<25}{self.desc:<50}"

